#!/usr/bin/env python3\n\"\"\"\nEnhanced Voice Demonstration for J.A.R.V.I.S\nDemonstrates realistic voice improvements\n\"\"\"\n\nimport sys\nimport os\nsys.path.append(os.path.dirname(os.path.dirname(os.path.abspath(__file__))))\n\nfrom jarvis.audio import TTS\nimport time\n\n\ndef demonstrate_voice_enhancements():\n    \"\"\"Demonstrate enhanced voice features\"\"\"\n    print(\"ðŸŽ­ J.A.R.V.I.S Enhanced Voice Demonstration\")\n    print(\"=\" * 50)\n    \n    # Test phrases with different characteristics\n    test_phrases = [\n        \"Hello, I am J.A.R.V.I.S., your enhanced AI assistant.\",\n        \"I now speak with improved naturalness and clarity.\",\n        \"Notice the enhanced pacing and more comfortable volume.\",\n        \"My voice has been optimized for better user experience.\",\n        \"The current time is 3:30 PM on a beautiful afternoon.\"\n    ]\n    \n    print(\"\\nðŸ”§ Testing Enhanced Voice Configuration...\")\n    \n    # Create enhanced TTS instance\n    enhanced_tts = TTS(\n        voice=\"Zira\",  # Use Zira for natural female voice\n        rate=165,      # Slower, more natural rate\n        volume=0.85    # Comfortable volume\n    )\n    \n    print(f\"\\nðŸŽ¤ Voice Status: {enhanced_tts.get_status() if hasattr(enhanced_tts, 'get_status') else 'Enhanced TTS Active'}\")\n    \n    for i, phrase in enumerate(test_phrases, 1):\n        print(f\"\\nðŸ“¢ Speaking phrase {i}/5:\")\n        print(f\"   \\\"{phrase}\\\"\")\n        \n        enhanced_tts.say(phrase)\n        \n        if i < len(test_phrases):\n            input(\"\\nâ³ Press Enter for next phrase...\")\n    \n    print(\"\\n\" + \"=\" * 50)\n    print(\"ðŸŽ‰ Enhanced voice demonstration complete!\")\n    print(\"\\nðŸ’¡ Key Improvements:\")\n    print(\"   â€¢ More natural speech rate (165 WPM vs 190 WPM)\")\n    print(\"   â€¢ Comfortable volume level (85% vs 100%)\")\n    print(\"   â€¢ Better voice selection (Zira for naturalness)\")\n    print(\"   â€¢ Enhanced pronunciation and pacing\")\n\n\ndef compare_voice_settings():\n    \"\"\"Compare different voice settings\"\"\"\n    print(\"\\nðŸ”€ Voice Settings Comparison\")\n    print(\"=\" * 40)\n    \n    test_text = \"Hello, this is J.A.R.V.I.S. How do I sound?\"\n    \n    settings = [\n        {\"name\": \"Original\", \"voice\": \"Zira\", \"rate\": 190, \"volume\": 1.0},\n        {\"name\": \"Enhanced\", \"voice\": \"Zira\", \"rate\": 165, \"volume\": 0.85},\n        {\"name\": \"Slower\", \"voice\": \"Zira\", \"rate\": 150, \"volume\": 0.80},\n        {\"name\": \"David Male\", \"voice\": \"David\", \"rate\": 165, \"volume\": 0.85}\n    ]\n    \n    for setting in settings:\n        print(f\"\\nðŸŽ¯ Testing {setting['name']} voice...\")\n        print(f\"   Voice: {setting['voice']}, Rate: {setting['rate']}, Volume: {setting['volume']}\")\n        \n        tts = TTS(\n            voice=setting['voice'],\n            rate=setting['rate'],\n            volume=setting['volume']\n        )\n        \n        tts.say(test_text)\n        \n        rating = input(f\"\\nâ­ Rate {setting['name']} voice (1-5): \")\n        print(f\"   You rated {setting['name']}: {rating}/5\")\n\n\ndef interactive_voice_tuning():\n    \"\"\"Interactive voice parameter tuning\"\"\"\n    print(\"\\nðŸŽ›ï¸ Interactive Voice Tuning\")\n    print(\"=\" * 35)\n    \n    current_voice = \"Zira\"\n    current_rate = 165\n    current_volume = 0.85\n    \n    test_text = \"Hello, I am J.A.R.V.I.S. How do I sound with these settings?\"\n    \n    while True:\n        print(f\"\\nðŸ“Š Current Settings:\")\n        print(f\"   Voice: {current_voice}\")\n        print(f\"   Rate: {current_rate} WPM\")\n        print(f\"   Volume: {current_volume}\")\n        \n        print(\"\\nðŸŽ›ï¸ Options:\")\n        print(\"1. Test current settings\")\n        print(\"2. Change voice (Zira/David)\")\n        print(\"3. Adjust rate (150-200 WPM)\")\n        print(\"4. Adjust volume (0.5-1.0)\")\n        print(\"5. Save optimal settings to .env\")\n        print(\"6. Exit\")\n        \n        choice = input(\"\\nâž¤ Choose option (1-6): \").strip()\n        \n        if choice == \"1\":\n            print(f\"\\nðŸ”Š Testing: {test_text}\")\n            tts = TTS(voice=current_voice, rate=current_rate, volume=current_volume)\n            tts.say(test_text)\n            \n        elif choice == \"2\":\n            print(\"\\nAvailable voices: Zira (female), David (male)\")\n            new_voice = input(\"Enter voice name: \").strip()\n            if new_voice:\n                current_voice = new_voice\n                print(f\"âœ… Voice changed to: {current_voice}\")\n                \n        elif choice == \"3\":\n            try:\n                new_rate = int(input(\"Enter new rate (150-200): \").strip())\n                if 150 <= new_rate <= 200:\n                    current_rate = new_rate\n                    print(f\"âœ… Rate changed to: {current_rate} WPM\")\n                else:\n                    print(\"âŒ Rate must be between 150-200\")\n            except ValueError:\n                print(\"âŒ Invalid rate entered\")\n                \n        elif choice == \"4\":\n            try:\n                new_volume = float(input(\"Enter new volume (0.5-1.0): \").strip())\n                if 0.5 <= new_volume <= 1.0:\n                    current_volume = new_volume\n                    print(f\"âœ… Volume changed to: {current_volume}\")\n                else:\n                    print(\"âŒ Volume must be between 0.5-1.0\")\n            except ValueError:\n                print(\"âŒ Invalid volume entered\")\n                \n        elif choice == \"5\":\n            try:\n                env_content = f\"\"\"# Jarvis Enhanced TTS configuration\nJARVIS_VOICE={current_voice}\nJARVIS_TTS_RATE={current_rate}\nJARVIS_TTS_VOLUME={current_volume}\n\"\"\"\n                with open(\".env\", \"w\") as f:\n                    f.write(env_content)\n                print(\"âœ… Settings saved to .env file!\")\n                print(\"ðŸ”„ Restart J.A.R.V.I.S to apply new settings.\")\n            except Exception as e:\n                print(f\"âŒ Error saving settings: {e}\")\n                \n        elif choice == \"6\":\n            print(\"\\nðŸ‘‹ Voice tuning complete!\")\n            break\n        else:\n            print(\"âŒ Invalid choice. Please select 1-6.\")\n\n\ndef main():\n    \"\"\"Main voice demonstration menu\"\"\"\n    print(\"ðŸŽ­ J.A.R.V.I.S Enhanced Voice System\")\n    print(\"====================================\\n\")\n    \n    while True:\n        print(\"Choose demonstration:\")\n        print(\"1. ðŸŽ¤ Enhanced Voice Demo\")\n        print(\"2. ðŸ”€ Voice Settings Comparison\")\n        print(\"3. ðŸŽ›ï¸ Interactive Voice Tuning\")\n        print(\"4. ðŸšª Exit\")\n        \n        choice = input(\"\\nâž¤ Enter choice (1-4): \").strip()\n        \n        if choice == \"1\":\n            demonstrate_voice_enhancements()\n        elif choice == \"2\":\n            compare_voice_settings()\n        elif choice == \"3\":\n            interactive_voice_tuning()\n        elif choice == \"4\":\n            print(\"\\nðŸ‘‹ Goodbye! Enjoy your enhanced J.A.R.V.I.S voice!\")\n            break\n        else:\n            print(\"âŒ Invalid choice. Please select 1-4.\")\n\n\nif __name__ == \"__main__\":\n    main()
