#!/usr/bin/env python3\n\"\"\"\nQuick Voice Enhancement Test for J.A.R.V.I.S\nTest enhanced voice settings immediately\n\"\"\"\n\nimport sys\nimport os\nsys.path.append(os.path.dirname(os.path.dirname(os.path.abspath(__file__))))\n\nfrom jarvis.audio import TTS\n\ndef test_enhanced_voice():\n    \"\"\"Test enhanced voice immediately\"\"\"\n    print(\"üé≠ J.A.R.V.I.S Enhanced Voice Test\")\n    print(\"=\" * 40)\n    \n    # Test current voice\n    print(\"\\n1Ô∏è‚É£ Testing CURRENT voice settings...\")\n    current_tts = TTS()\n    current_tts.say(\"Hello, this is how I currently sound.\")\n    \n    input(\"\\nPress Enter to test ENHANCED voice...\")\n    \n    # Test enhanced voice\n    print(\"\\n2Ô∏è‚É£ Testing ENHANCED voice settings...\")\n    enhanced_tts = TTS(voice=\"Zira\", rate=165, volume=0.85)\n    enhanced_tts.say(\"Hello, this is how I sound with enhanced, more natural settings.\")\n    \n    input(\"\\nPress Enter to test DIFFERENT voice...\")\n    \n    # Test different voice\n    print(\"\\n3Ô∏è‚É£ Testing MALE voice (David)...\")\n    male_tts = TTS(voice=\"David\", rate=160, volume=0.80)\n    male_tts.say(\"Hello, I am J.A.R.V.I.S with a male voice. How do I sound?\")\n    \n    print(\"\\n‚úÖ Voice test complete!\")\n    print(\"\\nüí° To use enhanced voice permanently:\")\n    print(\"   Update your .env file with:\")\n    print(\"   JARVIS_VOICE=Zira\")\n    print(\"   JARVIS_TTS_RATE=165\")\n    print(\"   JARVIS_TTS_VOLUME=0.85\")\n\nif __name__ == \"__main__\":\n    test_enhanced_voice()
